import React, { Component } from 'react';
import '../style.css';
import Colors from '../colors';

import Socials from '../components/Socials';

class Hero extends Component {
	constructor() {
		super();
		this.state = { screenWidth: null };
		this.updateWindowDimensions = this.updateWindowDimensions.bind(this);
	}

	componentDidMount() {
		window.addEventListener('resize', this.updateWindowDimensions());
	}

	componentWillUnmount() {
		window.removeEventListener('resize', this.updateWindowDimensions);
	}

	updateWindowDimensions() {
		this.setState({ screenWidth: window.innerWidth });
	}

	render() {
		return (
			<div className="hero-img">
				<div className="my-container between heroContainer" style={styles.heroContainer}>
					<div className="my-container column-dir ">
						<h1 style={styles.heroAnna} className="heroAnna">
							Anna Zhang
						</h1>
					</div>
					<div className="my-container column-dir heroText" style={styles.heroText}>
						<p style={styles.bio} className="bio">
							An undergrad CS student passionate about computer science, sustainability, and psychology.
						</p>
						<Socials color={Colors.white} />
					</div>
				</div>
			</div>
		);
	}
}

const styles = {};

export default Hero;
